mapper.select:
SELECT kw10000.item_code , kw10000.hash , COALESCE(prtt.trdd, '') AS trdd FROM t_kw_kw10000 kw10000 LEFT OUTER JOIN v_etf_tr40005_prtt prtt ON 1 = 1 AND kw10000.item_code = prtt.item_code AND prtt.rnk = 3 WHERE 1 = 1
AND kw10000.mrkt_type = #{mrkt_type}
AND kw10000.on_prgr = #{on_prgr}

mapper.update:
UPDATE t_kw_kw10000
SET
	on_prgr = #{on_prgr}
	, date_updt = NOW()
	, hash = #{hash}
	, item_name = #{item_name}
	, item_cnst = #{item_cnst}
	, item_stts = #{item_stts}
WHERE 1 = 1
	AND item_code = #{item_code}

